import React, { Component } from 'react';
import launchViewer from './ViewerFunctions';

class Viewer extends Component {

    // 
    componentDidMount() {
        const { selectedCollection, collectionDetails } = this.props;
        const urn = collectionDetails[selectedCollection]?.urn;
        if (urn) {
            const documentId = `urn:${urn}`;
            launchViewer('viewerDiv', documentId);
        }
    }
    
    render() {
        return (
            <div style={{position: "absolute", width: "100%", height: "85%"}} id="viewerDiv"/>
        );
    }
}

export default Viewer;
