import React, { Component } from 'react';
import { AppBar, Toolbar, Typography, IconButton, Button, FormControl, Select, Dialog, MenuItem, DialogTitle, DialogContentText, DialogContent, DialogActions, InputLabel } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import Viewer from './Viewer/Viewer';
import { getSelection } from './Viewer/ViewerFunctions';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
            doorFinish: "Satin",
            collections: [],
            selectedCollection: '',
            documents: []
        };
    }

    componentDidMount() {
        this.fetchCollections();
    }

    fetchCollections = () => {
        fetch('http://localhost:8080/zz')
            .then(response => response.json())
            .then(data => {
                if (data.collections) {
                    this.setState({ collections: data.collections });
                    // Lấy linkanh từ document đầu tiên và đặt nó là src của hình ảnh
                    if (data.collections.length > 0) {
                        const linkanh = data.collections[0].linkanh;
                        this.setState({ imageSrc: linkanh });
                    }
                } else {
                    console.error('Error: collections data not found in response:', data);
                }
            })
            .catch(error => console.error('Error fetching collections:', error));
    }

    handleClose = () => {
        this.setState({ open: false });
    }

    handleChange = (event) => {
        this.setState({ doorFinish: event.target.value });
    }

    handleClickOpen = () => {
        this.setState({ open: true });
    }

    handleCollectionChange = (event) => {
        const collectionName = event.target.value;
        this.setState({ selectedCollection: collectionName });
        this.fetchDocumentsInCollection(collectionName);
    }

    fetchDocumentsInCollection = (collectionName) => {
        if (collectionName) {
            fetch(`/collections/${collectionName}`)
                .then(response => response.json())
                .then(data => {
                    this.setState({ documents: data });
                })
                .catch(error => console.error('Error fetching documents:', error));
        } else {
            this.setState({ documents: [] });
        }
    }

    render() {
        const { open, doorFinish, collections, selectedCollection, documents, imageSrc } = this.state;

        return (
            <div className='viewer-home'>
                <AppBar position="static" style={{ marginBottom: 25 }}>
                    <Toolbar>
                        <IconButton
                            size="large"
                            edge="start"
                            color="inherit"
                            aria-label="menu"
                            sx={{ mr: 2 }}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                            Vina Tech
                        </Typography>
                        <Button color="inherit" onClick={this.handleClickOpen}>Update</Button>
                    </Toolbar>
                </AppBar>

                <div className="carousel-container">
                    <Carousel showArrows={true} autoPlay={false} infiniteLoop={true} showThumbs={false}>
                        {collections.map((collection, index) => (
                            <div key={index} className="carousel-item">
                                <img src={imageSrc} alt={collection} />
                                {console.log({imageSrc})}
                                <div className="carousel-caption">
                                    <h3>{collection}</h3>
                                    <p>Location placeholder</p> {/* Update this with actual location if available */}
                                </div>
                            </div>
                        ))}
                    </Carousel>
                </div>

                {/* <Viewer /> */}

                <Dialog
                    open={open}
                    onClose={this.handleClose}
                >
                    <DialogTitle>Door Finish Update</DialogTitle>
                    <DialogContent>
                        <DialogContentText>
                            Please select door finish value.
                        </DialogContentText>
                        <FormControl sx={{ m: 2, minWidth: 200 }} size="small">
                            <InputLabel>Collections</InputLabel>
                            <Select
                                value={selectedCollection}
                                label="Collections"
                                onChange={this.handleCollectionChange}
                            >
                                <MenuItem value="">Select a collection</MenuItem>
                                {collections.map(collection => (
                                    <MenuItem key={collection} value={collection}>{collection}</MenuItem>
                                ))}
                            </Select>
                        </FormControl>

                        {selectedCollection && (
                            <div>
                                <h2>Documents in {selectedCollection}</h2>
                                <pre>{JSON.stringify(documents, null, 2)}</pre>
                            </div>
                        )}
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={this.handleClose}>Cancel</Button>
                        <Button onClick={() => getSelection(doorFinish)}>Save</Button>
                    </DialogActions>
                </Dialog>
            </div>
        )
    }
}

export default Home;
